import React, { useState, useEffect } from "react";

export const GifGrid = ({ category }) => {
    const [count, setCount] = useState(0)
    useEffect(()=> {
        getGifs()
    },[])
  const getGifs = async () => {
    const url =
      "https://api.giphy.com/v1/gifs/search?api_key=EbA0GSgcDQI2e9HHhzTQswtFHhlU8UW4&q=metallica&limit=10";
    const resp = await fetch(url);
    const { data } = await resp.json();
    const gifs = data.map((img) => {
      return {
        img: img.id,
        title: img.title,
        url: img.url,
      };
    });
    console.log(gifs);
  };
  return (
    <>
      <h3>{category}</h3>
      <h3>{count}</h3>
      <button onClick={()=> setCount(count + 1)}></button>
    </>
  );
};
